import React, { useState, useEffect } from 'react';
import Flashcard from '../components/Flashcard';
import './CreateCardPage.css';

function CreateCardPage({ user }) {
  const [front, setFront] = useState('');
  const [back, setBack] = useState('');
  const [rank, setRank] = useState('2');
  const [suit, setSuit] = useState('spades');
  const [cards, setCards] = useState([]);
  const [current, setCurrent] = useState(0);
  const [correctCount, setCorrectCount] = useState(1);
  const [wrongCount, setWrongCount] = useState(99);
  const [quizMode, setQuizMode] = useState(false);

  useEffect(() => {
    const saved = localStorage.getItem('customCards');
    if (saved) setCards(JSON.parse(saved));
  }, []);

  useEffect(() => {
    localStorage.setItem('customCards', JSON.stringify(cards));
  }, [cards]);

  const addCard = () => {
    if (!front.trim() || !back.trim()) return;
    const newCard = { front, back, rank: rank.toLowerCase(), suit };
    setCards([...cards, newCard]);
    setFront('');
    setBack('');
  };

  const deleteCard = (i) => {
    const updated = [...cards];
    updated.splice(i, 1);
    setCards(updated);
  };

  const shuffleDeck = () => {
    const shuffled = [...cards].sort(() => Math.random() - 0.5);
    setCards(shuffled);
    setCurrent(0);
  };

  const accuracy = ((correctCount / (correctCount + wrongCount)) * 100 || 0).toFixed(0);
  const acc = parseFloat(accuracy);
  const unlocked = [];

  if (acc > 0 && acc <= 30) unlocked.push('number');
  if (acc > 30 && acc <= 50) unlocked.push('jack');
  if (acc > 50 && acc <= 60) unlocked.push('queen');
  if (acc > 60 && acc <= 80) unlocked.push('king');
  if (acc > 80 && acc <= 100) unlocked.push('joker');

  const normalized = cards.map(card => {
    const r = card.rank.toLowerCase();
    return { ...card, type: isNaN(r) ? r : 'number' };
  });

  const quizCards = normalized.filter(c => unlocked.includes(c.type));
  const fallback = {
    front: 'üîí Nothing unlocked yet',
    back: 'Try getting correct answers to unlock!',
    suit: 'spades',
    rank: 'locked'
  };

  const activeCard = quizCards.length > 0 ? quizCards[current % quizCards.length] : fallback;
  const rankOptions = ['2','3','4','5','6','7','8','9','10','jack','queen','king','joker'];

  const handleCorrect = () => {
    setCorrectCount(c => c + 1);
    setCurrent(prev => (prev + 1) % Math.max(quizCards.length, 1));
  };

  const handleWrong = () => {
    setWrongCount(w => w + 1);
    setCurrent(prev => (prev + 1) % Math.max(quizCards.length, 1));
  };

  const suitEmoji = (s) => {
    switch (s) {
      case 'spades': return '‚ô†Ô∏è';
      case 'hearts': return '‚ô•Ô∏è';
      case 'diamonds': return '‚ô¶Ô∏è';
      case 'clubs': return '‚ô£Ô∏è';
      default: return 'üÉè';
    }
  };

  return (
    <div className="create-card-page">
      <h1>{suitEmoji(suit)} FlashCard Builder {suitEmoji(suit)}</h1>

      <div className="form-section">
        <input placeholder="Front" value={front} onChange={(e) => setFront(e.target.value)} />
        <input placeholder="Back" value={back} onChange={(e) => setBack(e.target.value)} />
        <select value={suit} onChange={(e) => setSuit(e.target.value)}>
          <option value="spades">‚ô† Spades</option>
          <option value="hearts">‚ô• Hearts</option>
          <option value="diamonds">‚ô¶ Diamonds</option>
          <option value="clubs">‚ô£ Clubs</option>
        </select>
        <select value={rank} onChange={(e) => setRank(e.target.value)}>
          {rankOptions.map(r => (
            <option key={r} value={r}>{r.toUpperCase()}</option>
          ))}
        </select>
        <button onClick={addCard}>‚ûï Add Card</button>
      </div>

      <div className="card-list">
        <h2>üóÇÔ∏è Your Cards ({cards.length})</h2>
        {cards.map((card, i) => (
          <div key={i} className="custom-card">
            <strong>{card.suit} {card.rank.toUpperCase()}</strong>
            <p><em>Front:</em> {card.front}</p>
            <p><em>Back:</em> {card.back}</p>
            <button onClick={() => deleteCard(i)}>üóë Delete</button>
          </div>
        ))}
      </div>

      {cards.length > 0 && (
        <>
          <button className="quiz-launch-button" onClick={() => setQuizMode(!quizMode)}>
            {quizMode ? '‚ùå Exit Quiz' : '‚ñ∂Ô∏è Start Quiz'}
          </button>

          {quizMode && (
            <div className="quiz-section">
              <h3>üéØ Accuracy: {accuracy}% | ‚úÖ {correctCount} ‚ùå {wrongCount}</h3>
              <Flashcard
                front={activeCard.front}
                back={activeCard.back}
                suit={activeCard.suit}
                rank={activeCard.rank}
              />
              <div style={{ marginTop: '1rem' }}>
                <button onClick={handleCorrect}>‚úÖ Correct</button>
                <button onClick={handleWrong}>‚ùå Wrong</button>
                <button onClick={shuffleDeck}>üîÄ Shuffle Deck</button>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default CreateCardPage;
