import React, { useState } from 'react';
import decks from '../data/decks';
import Flashcard from '../components/Flashcard';
import './FlashcardPage.css';

function FlashcardPage({ user }) {
  const [suit, setSuit] = useState(null);
  const [cards, setCards] = useState([]);
  const [current, setCurrent] = useState(0);

  const chooseDeck = (chosenSuit) => {
    const selected = [...decks[chosenSuit]];
    setCards(shuffle(selected));
    setSuit(chosenSuit);
    setCurrent(0);
  };

  const shuffle = (arr) => arr.sort(() => Math.random() - 0.5);

  const nextCard = () => setCurrent((prev) => (prev + 1) % cards.length);

  return (
    <div className="page-wrapper">
      <header className="page-header">
        <h1 className="arcade-title">‚ô†Ô∏è FlashQuizApp ‚ô•Ô∏è</h1>
        <p className="tagline">Welcome, <strong>{user}</strong> üëã</p>
      </header>

     {!suit && (
  <div className="suit-grid">
    <h2 className="deck-title">Choose Your Knowledge Suit</h2>
    <div className="suit-options">
      <div className="suit-card" onClick={() => chooseDeck('spades')}>
        <span className="suit-icon">‚ô†Ô∏è</span>
        <h3>Spades</h3>
        <p>Physical & Action-Oriented Knowledge</p>
      </div>
      <div className="suit-card" onClick={() => chooseDeck('hearts')}>
        <span className="suit-icon">‚ô•Ô∏è</span>
        <h3>Hearts</h3>
        <p>Emotional & Psychological Knowledge</p>
      </div>
      <div className="suit-card" onClick={() => chooseDeck('diamonds')}>
        <span className="suit-icon">‚ô¶Ô∏è</span>
        <h3>Diamonds</h3>
        <p>Intellectual & Strategic Knowledge</p>
      </div>
      <div className="suit-card" onClick={() => chooseDeck('clubs')}>
        <span className="suit-icon">‚ô£Ô∏è</span>
        <h3>Clubs</h3>
        <p>Balanced & Social Knowledge</p>
      </div>
    </div>
  </div>
)}

    </div>
  );
}

export default FlashcardPage;
