import React, { useState } from 'react';
import decks from '../data/decks';
import Flashcard from '../components/Flashcard';
import './QuizPage.css';

function QuizPage({ user }) {
  const defaultSuit = 'spades';
  const [selectedSuit, setSelectedSuit] = useState(defaultSuit);
  const [cards, setCards] = useState([...decks[defaultSuit]]);
  const [current, setCurrent] = useState(0);
  const [correctCount, setCorrectCount] = useState(1);
  const [wrongCount, setWrongCount] = useState(99);
  const [showAll, setShowAll] = useState(false);
  const [flippedReward, setFlippedReward] = useState(false);
  const [flippedStats, setFlippedStats] = useState(false);

  const accuracy = ((correctCount / (correctCount + wrongCount)) * 100 || 0).toFixed(0);
  const acc = parseFloat(accuracy);

  const level =
    acc >= 100 ? '‚ô† Ace' :
    acc >= 75 ? '‚ô¶ Prodigy' :
    acc >= 50 ? '‚ô• Rookie' : '‚ô£ Novice';

  const unlockedRanks = [];
  const unlockedTypes = [];

  if (acc > 0 && acc <= 30) {
    unlockedRanks.push('Number Cards');
    unlockedTypes.push('number');
  }
  if (acc > 30 && acc <= 50) {
    unlockedRanks.push('Jack');
    unlockedTypes.push('jack');
  }
  if (acc > 50 && acc <= 60) {
    unlockedRanks.push('Queen');
    unlockedTypes.push('queen');
  }
  if (acc > 60 && acc <= 80) {
    unlockedRanks.push('King');
    unlockedTypes.push('king');
  }
  if (acc > 80 && acc <= 100) {
    unlockedRanks.push('Joker');
    unlockedTypes.push('joker');
  }

  const normalizedCards = cards.map(card => {
    const r = card.rank?.toLowerCase();
    return {
      ...card,
      type: isNaN(r) ? r : 'number'
    };
  });

  const visibleCards = normalizedCards.filter(card =>
    unlockedTypes.includes(card.type)
  );

  const fallbackCard = {
    front: 'üîí No cards unlocked yet!',
    back: 'Answer correctly to unlock content!',
    suit: selectedSuit,
    rank: 'locked'
  };

  const activeCard = visibleCards.length > 0
    ? visibleCards[current % visibleCards.length]
    : fallbackCard;

  const nextCard = () => setCurrent(prev => (prev + 1) % Math.max(visibleCards.length, 1));
  const prevCard = () => setCurrent(prev => (prev - 1 + Math.max(visibleCards.length, 1)) % Math.max(visibleCards.length, 1));
  const handleCorrect = () => {
    setCorrectCount(c => c + 1);
    nextCard();
  };
  const handleWrong = () => {
    setWrongCount(w => w + 1);
    nextCard();
  };
  const handleSuitChange = (suit) => {
    const newDeck = [...decks[suit]];
    setSelectedSuit(suit);
    setCards(newDeck);
    setCurrent(0);
  };
  const shuffleDeck = () => {
    const shuffled = [...cards].sort(() => Math.random() - 0.5);
    setCards(shuffled);
    setCurrent(0);
  };

  const suitEmoji = (suit) => {
    switch (suit) {
      case 'spades': return '‚ô†Ô∏è';
      case 'hearts': return '‚ô•Ô∏è';
      case 'diamonds': return '‚ô¶Ô∏è';
      case 'clubs': return '‚ô£Ô∏è';
      default: return 'üÉè';
    }
  };

  return (
    <div className="quiz-page">
      <header className="quiz-header">
        <h1>Quiz Mode üÉè</h1>
        <p>Welcome, <strong>{user}</strong></p>
      </header>

      <div className="quiz-layout">
        {/* Section 1: Reward Card */}
        <div 
          className={`flip-container ${flippedReward ? 'flipped' : ''}`}
          onClick={() => setFlippedReward(!flippedReward)}
        >
          <div className="flipper">
            <div className="front">
              <h3>REWARD</h3>
              <div className="reward-level">
                <span>II</span> 1-30%: Number cards
              </div>
            </div>
            <div className="back">
              <h3>Reward System</h3>
              <ul>
                <li><span>II</span> 30-50%: + Jack</li>
                <li><span>A</span> 50-60%: + Queen</li>
                <li><span>A</span> 80-100%: + Joker</li>
              </ul>
            </div>
          </div>
        </div>

        {/* Section 2: Spades & Hearts */}
        <div className="suit-column">
          <div 
            className={`suit-card ${selectedSuit === 'spades' ? 'active' : ''}`}
            onClick={() => handleSuitChange('spades')}
          >
            <span className="suit-icon">‚ô†</span>
            <h4>Spades</h4>
            <p>Physical</p>
          </div>
          <div 
            className={`suit-card ${selectedSuit === 'hearts' ? 'active' : ''}`}
            onClick={() => handleSuitChange('hearts')}
          >
            <span className="suit-icon">‚ô•</span>
            <h4>Hearts</h4>
            <p>Emotional</p>
          </div>
        </div>

        {/* Sections 3-5: Main Flashcard Area */}
        <div className="flashcard-area">
          <div className="card-controls">
            <button onClick={shuffleDeck}>üîÄ Shuffle</button>
            <button onClick={() => setShowAll(!showAll)}>
              {showAll ? 'üìá Hide All' : 'üìá Show All'}
            </button>
          </div>

          <div className="flashcard-wrapper">
            <Flashcard
              front={activeCard.front}
              back={activeCard.back}
              suit={activeCard.suit}
              rank={activeCard.rank}
              emoji={suitEmoji(activeCard.suit)}
            />
          </div>

          <div className="navigation-controls">
            <button onClick={prevCard}>‚¨ÖÔ∏è</button>
            <button className="correct-btn" onClick={handleCorrect}>‚úÖ</button>
            <span className="card-counter">
              {current + 1}/{visibleCards.length}
            </span>
            <button className="wrong-btn" onClick={handleWrong}>‚ùå</button>
            <button onClick={nextCard}>‚û°Ô∏è</button>
          </div>
        </div>

        {/* Section 6: Diamonds & Clubs */}
        <div className="suit-column">
          <div 
            className={`suit-card ${selectedSuit === 'diamonds' ? 'active' : ''}`}
            onClick={() => handleSuitChange('diamonds')}
          >
            <span className="suit-icon">‚ô¶</span>
            <h4>Diamonds</h4>
            <p>Intellectual</p>
          </div>
          <div 
            className={`suit-card ${selectedSuit === 'clubs' ? 'active' : ''}`}
            onClick={() => handleSuitChange('clubs')}
          >
            <span className="suit-icon">‚ô£</span>
            <h4>Clubs</h4>
            <p>Social</p>
          </div>
        </div>

        {/* Section 7: Stats Card */}
        <div 
          className={`flip-container ${flippedStats ? 'flipped' : ''}`}
          onClick={() => setFlippedStats(!flippedStats)}
        >
          <div className="flipper">
            <div className="front">
              <h3>STATS</h3>
              <p>Accuracy: {accuracy}%</p>
              <p>Level: {level}</p>
            </div>
            <div className="back">
              <h3>Details</h3>
              <p>Unlocked: {unlockedRanks.join(', ') || 'None'}</p>
              <p>Total: {cards.length}</p>
              <p>‚úÖ: {correctCount}</p>
              <p>‚ùå: {wrongCount}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

