import React, { useState } from 'react';
import decks from '../data/decks';
import Flashcard from '../components/Flashcard';
import './QuizPage.css';

function FlashcardQuizPage({ user }) {
  const [selectedSuit, setSelectedSuit] = useState('spades');
  const [cards, setCards] = useState([...decks['spades']]);
  const [current, setCurrent] = useState(0);
  const [correctCount, setCorrectCount] = useState(1);
  const [wrongCount, setWrongCount] = useState(99);
  const [showAll, setShowAll] = useState(false);
  const [flippedReward, setFlippedReward] = useState(false);
  const [flippedStats, setFlippedStats] = useState(false);

  // Calculate accuracy and level
  const accuracy = (correctCount / (correctCount + wrongCount)) * 100 || 0;
  const accuracyDisplay = accuracy.toFixed(0);
  
  const level =
    accuracy >= 100 ? '♠ Ace' :
    accuracy >= 75 ? '♦ Prodigy' :
    accuracy >= 50 ? '♥ Rookie' : '♣ Novice';

  // Determine unlocked cards
  const unlockedCards = [];
  if (accuracy > 0 && accuracy <= 30) {
    unlockedCards.push('Number Cards');
  }
  if (accuracy > 30 && accuracy <= 50) {
    unlockedCards.push('Jack Cards');
  }
  if (accuracy > 50 && accuracy <= 60) {
    unlockedCards.push('Queen Cards');
  }
  if (accuracy > 60 && accuracy <= 80) {
    unlockedCards.push('King Cards');
  }
  if (accuracy > 80) {
    unlockedCards.push('Joker');
  }

  const handleSuitChange = (suit) => {
    setSelectedSuit(suit);
    const shuffled = [...decks[suit]];
    setCards(shuffled);
    setCurrent(0);
    setShowAll(false);
  };

  const nextCard = () => setCurrent((prev) => (prev + 1) % cards.length);
  const prevCard = () => setCurrent((prev) => (prev - 1 + cards.length) % cards.length);
  
  const shuffleDeck = () => {
    const shuffled = [...cards].sort(() => Math.random() - 0.5);
    setCards(shuffled);
    setCurrent(0);
  };

  const handleCorrect = () => {
    setCorrectCount(c => c + 1);
    nextCard();
  };

  const handleWrong = () => {
    setWrongCount(w => w + 1);
    nextCard();
  };

  return (
    <div className="page-wrapper">
      <header className="page-header">
        <h1 className="arcade-title" style={{ color: 'gray', textAlign: 'center' }}>♠️ FlashQuizApp ♥️</h1>
        <p className="tagline">Welcome, <strong>{user}</strong> 👋</p>
      </header>

      <div className="quiz-layout">
        {/* Left Side - Reward Card */}
        <div 
          className={`flip-card ${flippedReward ? 'flipped' : ''}`}
          onClick={() => setFlippedReward(!flippedReward)}
        >
          <div className="flip-card-inner">
            <div className="flip-card-front">
              <h3>REWARD</h3>
              <div className="reward-item">
                <span>🂠</span> 1–30% → Number cards
              </div>
            </div>
            <div className="flip-card-back">
              <h3>Reward System</h3>
              <div className="reward-item"><span>🂫</span> 30–50% → + Jack</div>
              <div className="reward-item"><span>👸</span> 50–60% → + Queen</div>
              <div className="reward-item"><span>🤴</span> 60–80% → + King</div>
              <div className="reward-item"><span>🃏</span> 80–100% → + Joker</div>
            </div>
          </div>
        </div>

        {/* Center Area - Suit Selection and Flashcards */}
        <div className="center-area">
          {/* 🎴 Suit Selection Cards */}
          <div className="suit-split-layout">
            <div className="suit-side left">
              <div className="suit-card" onClick={() => handleSuitChange('spades')}>
                <span className="suit-icon">♠</span>
                <h3>Spades</h3>
                <p>Physical & Action-Oriented</p>
              </div>
              <div className="suit-card" onClick={() => handleSuitChange('hearts')}>
                <span className="suit-icon">♥</span>
                <h3>Hearts</h3>
                <p>Emotional & Psychological</p>
              </div>
            </div>

            <div className="suit-side right">
              <div className="suit-card" onClick={() => handleSuitChange('diamonds')}>
                <span className="suit-icon">♦</span>
                <h3>Diamonds</h3>
                <p>Intellectual & Strategic</p>
              </div>
              <div className="suit-card" onClick={() => handleSuitChange('clubs')}>
                <span className="suit-icon">♣</span>
                <h3>Clubs</h3>
                <p>Balanced & Social</p>
              </div>
            </div>
          </div>

          {/* 🔁 Deck Controls */}
          <div className="deck-toolbar">
            <button onClick={shuffleDeck}>🔀 Shuffle</button>
            <button onClick={() => setShowAll((prev) => !prev)}>
              {showAll ? '📇 Hide All Cards' : '📇 Show All Cards'}
            </button>
          </div>

          {/* 🧠 Flashcard Area */}
          {!showAll ? (
            <div className="flashcard-zone">
              <div className="flashcard-wrapper">
                <Flashcard
                  front={cards[current].front}
                  back={cards[current].back}
                  suit={selectedSuit}
                  rank={cards[current].rank}
                />
              </div>
              <div className="quiz-controls">
                <button onClick={prevCard}>⬅️ Prev</button>
                <button className="correct-btn" onClick={handleCorrect}>✅ Correct</button>
                <span className="card-counter">
                  Card {current + 1} / {cards.length}
                </span>
                <button className="wrong-btn" onClick={handleWrong}>❌ Wrong</button>
                <button onClick={nextCard}>Next ➡️</button>
              </div>
            </div>
          ) : (
            <div className="card-gallery">
              {cards.map((card, index) => (
                <Flashcard
                  key={index}
                  front={card.front}
                  back={card.back}
                  suit={selectedSuit}
                  rank={card.rank}
                />
              ))}
            </div>
          )}
        </div>

        {/* Right Side - Stats Card */}
        <div 
          className={`flip-card ${flippedStats ? 'flipped' : ''}`}
          onClick={() => setFlippedStats(!flippedStats)}
        >
          <div className="flip-card-inner">
            <div className="flip-card-front">
              <h3>📊 STATS</h3>
              <p>🎯 Accuracy: {accuracyDisplay}%</p>
              <p>🚀 Level: {level}</p>
            </div>
            <div className="flip-card-back">
              <h3>Detailed Stats</h3>
              <p>📑 Unlocked:</p>
              <p>{unlockedCards.join(', ') || 'None'}</p>
              <p>Total: {cards.length}</p>
              <p>✅ Correct: {correctCount}</p>
              <p>❌ Wrong: {wrongCount}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default FlashcardQuizPage;
