import React, { useState } from 'react';
import Flashcard from '../components/Flashcard';
import decks from '../data/decks'; // assumes card data is organized by suit
import './FlashcardPage.css';

function FlashcardPage({ user }) {
  const [currentSuit, setCurrentSuit] = useState('spades');
  const [cards, setCards] = useState(decks[currentSuit]);
  const [index, setIndex] = useState(0);

  const handleSuitChange = (suit) => {
    setCurrentSuit(suit);
    setCards(decks[suit]);
    setIndex(0);
  };

  const nextCard = () => setIndex((prev) => (prev + 1) % cards.length);
  const prevCard = () => setIndex((prev) => (prev - 1 + cards.length) % cards.length);

  const currentCard = cards[index];

  return (
    <div className="flashcard-page">
      <header>
        <h1>üÉè Flashcard Deck</h1>
        <p>Hello, <strong>{user}</strong></p>
      </header>

      <div className="suit-selector">
        {['spades', 'hearts', 'diamonds', 'clubs'].map((suit) => (
          <button
            key={suit}
            className={`suit-btn ${currentSuit === suit ? 'active' : ''}`}
            onClick={() => handleSuitChange(suit)}
          >
            {suit.charAt(0).toUpperCase() + suit.slice(1)}
          </button>
        ))}
      </div>

      <div className="card-display">
        <button onClick={prevCard}>‚¨ÖÔ∏è</button>
        <Flashcard
          front={currentCard.front}
          back={currentCard.back}
          suit={currentCard.suit}
          rank={currentCard.rank}
        />
        <button onClick={nextCard}>‚û°Ô∏è</button>
      </div>

      <p className="counter">Card {index + 1} of {cards.length}</p>
    </div>
  );
}

export default FlashcardPage;
