import React, { useState, useEffect } from 'react';
import './CreateCardPage.css';

function CreateCardPage() {
  const [cards, setCards] = useState([]);
  const [front, setFront] = useState('');
  const [back, setBack] = useState('');
  const [quizIndex, setQuizIndex] = useState(0);
  const [userAnswer, setUserAnswer] = useState('');
  const [resultMessage, setResultMessage] = useState('');
  const [showTest, setShowTest] = useState(false);

  useEffect(() => {
    const savedCards = localStorage.getItem('flashcards');
    if (savedCards) {
      try {
        const parsed = JSON.parse(savedCards);
        if (Array.isArray(parsed)) {
          setCards(parsed);
        }
      } catch (err) {
        console.error('Invalid card data in localStorage.');
      }
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('flashcards', JSON.stringify(cards));
  }, [cards]);

  const addCard = () => {
    if (!front.trim() || !back.trim()) return;
    const newCard = { front, back };
    const updated = [...cards, newCard];
    setCards(updated);
    setFront('');
    setBack('');
  };

  const deleteCard = (i) => {
    const updated = [...cards];
    updated.splice(i, 1);
    setCards(updated);
    if (quizIndex >= i && quizIndex > 0) {
      setQuizIndex((prev) => prev - 1);
    }
  };

  const clearAll = () => {
    if (window.confirm('Are you sure you want to delete all flashcards?')) {
      setCards([]);
      setQuizIndex(0);
      setUserAnswer('');
      setShowTest(false);
      setResultMessage('');
      localStorage.removeItem('flashcards');
    }
  };

  const startTest = () => {
    if (cards.length === 0) {
      alert('Please create some flashcards first.');
      return;
    }
    setQuizIndex(0);
    setUserAnswer('');
    setResultMessage('');
    setShowTest(true);
  };

  const checkAnswer = () => {
    const correct = cards[quizIndex].back.trim().toLowerCase();
    const answer = userAnswer.trim().toLowerCase();
    if (answer === correct) {
      setResultMessage('‚úÖ Correct!');
    } else {
      setResultMessage(`‚ùå Incorrect. Correct answer: ${cards[quizIndex].back}`);
    }
  };

  const nextCard = () => {
    if (quizIndex + 1 < cards.length) {
      setQuizIndex(quizIndex + 1);
      setUserAnswer('');
      setResultMessage('');
    } else {
      alert('Test complete!');
      setShowTest(false);
    }
  };

  return (
    <div className="app-container">
      <header>
        <h1>Create Flashcards</h1>
        <div className="mode-toggle">
          <a href="/" className="mode-btn">üè† Home</a>
          <button onClick={startTest} className="mode-btn">üß™ Test</button>
        </div>
      </header>

      <div className="main-content">
        <section>
          <h2>Create New Flashcard</h2>
          <textarea rows="3" placeholder="Enter question or term" value={front} onChange={(e) => setFront(e.target.value)} />
          <textarea rows="3" placeholder="Enter answer or definition" value={back} onChange={(e) => setBack(e.target.value)} />
          <div className="management-buttons">
            <button onClick={addCard} className="primary-btn">‚ûï Add Flashcard</button>
            <button onClick={clearAll} className="danger-btn">üóë Clear All</button>
          </div>
          <p>Total Cards: <strong>{cards.length}</strong></p>
        </section>

        <section className="card-list">
          {cards.length === 0 ? (
            <p>No flashcards created yet.</p>
          ) : (
            cards.map((card, index) => (
              <div className="card-item" key={index}>
                <div className="card-content">
                  <strong>Q:</strong> {card.front}<br />
                  <strong>A:</strong> {card.back}
                </div>
                <button className="delete-card-btn" onClick={() => deleteCard(index)}>üóë</button>
              </div>
            ))
          )}
        </section>

        {showTest && cards.length > 0 && (
          <section className="test-section">
            <h2>Test Yourself</h2>
            <p><strong>Question:</strong> {cards[quizIndex].front}</p>
            <input
              type="text"
              className="answer-input"
              placeholder="Type your answer here..."
              value={userAnswer}
              onChange={(e) => setUserAnswer(e.target.value)}
            />
            <button onClick={checkAnswer} className="check-answer-btn">Check Answer</button>
            {resultMessage && <p className={`result-message ${resultMessage.includes('‚úÖ') ? 'correct' : 'incorrect'}`}>{resultMessage}</p>}
            <button onClick={nextCard} className="primary-btn" style={{ marginTop: '10px' }}>‚û°Ô∏è Next</button>
          </section>
        )}
      </div>
    </div>
  );
}

export default CreateCardPage;
