import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './NavMenu.css';

function NavMenu() {
  const [showFeatures, setShowFeatures] = useState(false);
  const [theme, setTheme] = useState('dark');
  const location = useLocation();

  useEffect(() => {
    document.body.className = theme + '-theme';
  }, [theme]);

  return (
    <>
      <header className="navbar">
        <div className="nav-left">
          <div className="hamburger" onClick={() => setShowFeatures(!showFeatures)}>‚ò∞</div>
        </div>
        <div className="nav-right">
          <Link to="/">Flashcards</Link>
          <Link to="/quiz">Quiz</Link>
          <Link to="/create">Create</Link>
          <Link to="/about">About</Link>
        </div>
      </header>

      <div className="main-layout">
        {showFeatures && (
          <aside className="feature-panel">
            <h3>‚ú® Features</h3>
            <ul>
              <li>üÉè Card flipping</li>
              <li>üé® Dark/Mid/Light themes</li>
              <li>üîÅ Shuffle & filter by suit</li>
              <li>üíæ Create your own cards</li>
            </ul>
            <button className="theme-toggle" onClick={() => {
              setTheme(prev => prev === 'dark' ? 'light' : 'dark');
            }}>
              {theme === 'dark' ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
            </button>
          </aside>
        )}

        <main className="page-content">
          {/* Content from pages will appear here via App.js */}
        </main>
      </div>
    </>
  );
}

export default NavMenu;
