import React, { useState, useEffect } from 'react';
import './CreateCardPage.css';

function CreateCardPage() {
  const [cards, setCards] = useState([]);
  const [front, setFront] = useState('');
  const [back, setBack] = useState('');
  const [quizIndex, setQuizIndex] = useState(0);
  const [userAnswer, setUserAnswer] = useState('');
  const [showAnswer, setShowAnswer] = useState(false);

  // 🔄 Load from localStorage on first load
  useEffect(() => {
    const savedCards = localStorage.getItem('myFlashcards');
    if (savedCards) {
      try {
        const parsed = JSON.parse(savedCards);
        if (Array.isArray(parsed)) {
          setCards(parsed);
        }
      } catch (err) {
        console.error('Invalid card data in localStorage.');
      }
    }
  }, []);

  // 💾 Save to localStorage on every change
  useEffect(() => {
    localStorage.setItem('myFlashcards', JSON.stringify(cards));
  }, [cards]);

 const addCard = () => {
  if (!front.trim() || !back.trim()) return;
  const newCard = { front, back };
  const updated = [...cards, newCard];
  setCards(updated);
  localStorage.setItem('myFlashcards', JSON.stringify(updated)); // 🔥 manually store it right here
  setFront('');
  setBack('');
};


  const deleteCard = (i) => {
    const updated = [...cards];
    updated.splice(i, 1);
    setCards(updated);
  };

  const clearAll = () => {
    setCards([]);
    setQuizIndex(0);
    setUserAnswer('');
    setShowAnswer(false);
    localStorage.removeItem('myFlashcards');
  };

  const nextQuizCard = () => {
    setUserAnswer('');
    setShowAnswer(false);
    setQuizIndex((prev) => (prev + 1) % (cards.length || 1));
  };

  return (
    <div className="create-page">
      <h1>🧠 Flashcard Builder + Quiz</h1>

      <section className="create-section">
        <h2>Create New Flashcard</h2>
        <input
          type="text"
          placeholder="Front (Question)"
          value={front}
          onChange={(e) => setFront(e.target.value)}
        />
        <input
          type="text"
          placeholder="Back (Answer)"
          value={back}
          onChange={(e) => setBack(e.target.value)}
        />
        <div className="button-group">
          <button onClick={addCard}>➕ Add Card</button>
          <button className="clear-button" onClick={clearAll}>🗑 Clear All</button>
        </div>
      </section>

      <section className="card-list">
        <h2>📚 Your Cards ({cards.length})</h2>
        <ul>
          {cards.map((card, index) => (
            <li key={index}>
              <div>
                <strong>Q:</strong> {card.front}<br />
                <strong>A:</strong> {card.back}
              </div>
              <button onClick={() => deleteCard(index)}>❌</button>
            </li>
          ))}
        </ul>
      </section>

      {cards.length > 0 && (
        <section className="quiz-section">
          <h2>📝 Quiz Mode</h2>
          <p><strong>Question:</strong> {cards[quizIndex].front}</p>
          <input
            type="text"
            placeholder="Your answer..."
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
          />
          {showAnswer && (
            <p className="correct-answer">
              ✅ Correct Answer: <strong>{cards[quizIndex].back}</strong>
            </p>
          )}
          <div className="quiz-controls">
            <button onClick={() => setShowAnswer(true)}>👁 Show Answer</button>
            <button onClick={nextQuizCard}>➡️ Next</button>
          </div>
        </section>
      )}
    </div>
  );
}

export default CreateCardPage;
