import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './NavMenu.css';

function NavMenu() {
  const [open, setOpen] = useState(false);
  const [theme, setTheme] = useState('dark');
  const location = useLocation();

  useEffect(() => {
    document.body.className = theme + '-theme';
  }, [theme]);

  useEffect(() => {
    setOpen(false); // close menu on navigation
  }, [location.pathname]);

  return (
    <>
      <div className="topbar">
        <div className="nav-left">
          <button className="hamburger" onClick={() => setOpen(!open)}>‚ò∞</button>
        </div>
        <div className="nav-right">
          <Link to="/">Flashcards</Link>
          <Link to="/quiz">Quiz</Link>
          <Link to="/create">Create</Link>
          <Link to="/about">About</Link>
          <button className="theme-toggle" onClick={() => setTheme(prev => prev === 'dark' ? 'light' : 'dark')}>
            {theme === 'dark' ? '‚òÄÔ∏è' : 'üåô'}
          </button>
        </div>
      </div>

      <div className={`sidebar-wrapper ${open ? 'open' : ''}`}>
        <div className="sidebar">
          <h2>‚ú® Features</h2>
          <ul>
            <li>üÉè Card flipping animations</li>
            <li>üé® Theme switching (Dark/Light/Mid)</li>
            <li>üîÄ Category filters and shuffle</li>
            <li>üß† Add your own flashcards</li>
          </ul>
        </div>
      </div>
    </>
  );
}

export default NavMenu;
