import React, { useState, useEffect } from 'react';
import { FaGithub, FaLinkedin, FaFacebook, FaGlobe } from 'react-icons/fa';
import './AboutPage.css';

function AboutPage() {
  const [flippedCards, setFlippedCards] = useState({
    about: false,
    usage: false,
    creator: false,
    tech: false
  });

  const [isDealing, setIsDealing] = useState(true);
  const [magicActive, setMagicActive] = useState(false);

  const toggleCard = (card) => {
    setFlippedCards(prev => ({
      ...prev,
      [card]: !prev[card]
    }));
    setMagicActive(true);
    setTimeout(() => setMagicActive(false), 1000);
  };

  useEffect(() => {
    const timer = setTimeout(() => setIsDealing(false), 1500);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="magic-container">
      <div className="magic-stage">
        {/* Magician's Table Surface */}
        <div className="magic-table">
          {/* Magician's Hand Holding Cards */}
          <div className="magician-hand">
            <div className="profile-holder">
              <img src="/images/masrafi.jpg.png" alt="Masrafi" className="magician-profile" />
              <div className="hand-shadow"></div>
            </div>
            
            {/* Social Links as Cards in Hand */}
            <div className="social-cards">
              <a href="https://github.com/masrafi" className="social-card github">
                <FaGithub className="social-icon" />
              </a>
              <a href="https://linkedin.com/in/masrafi" className="social-card linkedin">
                <FaLinkedin className="social-icon" />
              </a>
              <a href="https://facebook.com/masrafi" className="social-card facebook">
                <FaFacebook className="social-icon" />
              </a>
              <a href="https://your-portfolio.com" className="social-card portfolio">
                <FaGlobe className="social-icon" />
              </a>
            </div>
          </div>

          {/* Main Card Grid */}
          <div className={`card-grid ${magicActive ? 'magic-active' : ''}`}>
            {['about', 'usage', 'creator', 'tech'].map((card, index) => (
              <div 
                key={card}
                className={`magic-card ${flippedCards[card] ? 'flipped' : ''}`}
                onClick={() => toggleCard(card)}
                style={{ 
                  '--deal-delay': `${index * 0.2}s`,
                  '--card-color': ['#4a6bff', '#6d5bff', '#ff6b6b', '#50c9ba'][index]
                }}
              >
                <div className="card-inner">
                  <div className="card-front">
                    <div className="card-corner top-left">
                      {['?', '✧', '✦', '✶'][index]}
                    </div>
                    <div className="card-corner bottom-right">
                      {['?', '✧', '✦', '✶'][index]}
                    </div>
                    <div className="card-symbol">
                      {['?', '✧', '✦', '✶'][index]}
                    </div>
                    <h3>{[
                      'What is FlashQuiz?', 
                      'How to Use', 
                      'The Creator', 
                      'Tech Stack'
                    ][index]}</h3>
                  </div>
                  <div className="card-back">
                    <div className="card-back-design"></div>
                    <div className="card-content">
                      {card === 'about' && (
                        <p>FlashQuiz transforms learning into an elegant performance through card-based memory magic.</p>
                      )}
                      {card === 'usage' && (
                        <ul>
                          <li>Create custom card decks</li>
                          <li>Study with magical repetition</li>
                          <li>Track mastery levels</li>
                        </ul>
                      )}
                      {card === 'creator' && (
                        <>
                          <p><strong>Masrafi</strong> - Developer and memory magician creating elegant learning tools.</p>
                          <div className="mini-links">
                            <FaGithub className="mini-icon" />
                            <FaLinkedin className="mini-icon" />
                            <FaFacebook className="mini-icon" />
                            <FaGlobe className="mini-icon" />
                          </div>
                        </>
                      )}
                      {card === 'tech' && (
                        <ul className="tech-stack">
                          <li><span>ReactJS</span> - UI Magic</li>
                          <li><span>Framer Motion</span> - Animations</li>
                          <li><span>IndexedDB</span> - Storage</li>
                        </ul>
                      )}
                    </div>
                  </div>
                </div>
                <div className="card-shine"></div>
              </div>
            ))}
          </div>
        </div>

        {/* Footer with floating cards */}
        <div className="magic-footer">
          <div className="floating-cards">
            {[...Array(5)].map((_, i) => (
              <div key={i} className="floater" style={{ '--delay': `${i * 0.3}s` }}></div>
            ))}
          </div>
          <p>No accounts • No ads • Pure learning magic</p>
        </div>
      </div>
    </div>
  );
}

export default AboutPage;
