import React, { useState } from 'react';
import { FaShuffle, FaRotate } from 'react-icons/fa6';
import './QuizPage.css';

function QuizPage({ user }) {
  // Existing state management
  const [selectedSuit, setSelectedSuit] = useState('spades');
  const [currentCard, setCurrentCard] = useState(0);
  const [correctCount, setCorrectCount] = useState(1);
  const [wrongCount, setWrongCount] = useState(99);
  const [isFlipped, setIsFlipped] = useState(false);
  const [cards, setCards] = useState([]); // Added cards state
  
  // New state for flipping reward/stats cards
  const [flippedCards, setFlippedCards] = useState({
    reward: false,
    stats: false
  });

  // Handler functions
  const handleShuffle = () => {
    setCards(prevCards => [...prevCards].sort(() => Math.random() - 0.5));
    setCurrentCard(0);
    setIsFlipped(false);
  };

  const handleCorrect = () => {
    setCorrectCount(prev => prev + 1);
    setCurrentCard(prev => (prev + 1) % cards.length);
    setIsFlipped(false);
  };

  const handleWrong = () => {
    setWrongCount(prev => prev + 1);
    setCurrentCard(prev => (prev + 1) % cards.length);
    setIsFlipped(false);
  };

  // Calculate accuracy and level
  const accuracy = ((correctCount / (correctCount + wrongCount)) * 100 || 0).toFixed(0);
  const level = accuracy >= 100 ? '♠ Ace' :
                accuracy >= 75 ? '♦ Prodigy' :
                accuracy >= 50 ? '♥ Rookie' : '♣ Novice';

  // Toggle flip for reward/stats cards
  const toggleFlip = (card) => {
    setFlippedCards(prev => ({
      ...prev,
      [card]: !prev[card]
    }));
  };

  return (
    <div className="quiz-container">
      <header className="quiz-header">
        <h1>FlashQuiz</h1>
        <p>Welcome, <strong>{user}</strong></p>
      </header>

      <div className="quiz-layout">
        {/* Left Panel - Suit Selection */}
        <div className="suit-panel">
          <button 
            className={`suit-btn ${selectedSuit === 'spades' ? 'active' : ''}`}
            onClick={() => setSelectedSuit('spades')}
          >
            <span>♠</span> Spades
            <small>Physical Knowledge</small>
          </button>
          
          <button 
            className={`suit-btn ${selectedSuit === 'hearts' ? 'active' : ''}`}
            onClick={() => setSelectedSuit('hearts')}
          >
            <span>♥</span> Hearts
            <small>Emotional Knowledge</small>
          </button>
        </div>

        {/* Center Panel - Main Flashcard */}
        <div className="main-card-area">
          <div className="card-controls">
            <button onClick={() => setIsFlipped(!isFlipped)}>
              <FaRotate /> Flip
            </button>
            <button onClick={handleShuffle}>
              <FaShuffle /> Shuffle
            </button>
          </div>

          <div className={`flashcard ${isFlipped ? 'flipped' : ''}`}>
            <div className="flashcard-inner">
              <div className="flashcard-front">
                <h3>Which bike brand is known for the model "RIST"?</h3>
              </div>
              <div className="flashcard-back">
                <p>Answer: The correct answer would appear here</p>
              </div>
            </div>
          </div>

          <div className="answer-buttons">
            <button className="correct-btn" onClick={handleCorrect}>Correct</button>
            <button className="wrong-btn" onClick={handleWrong}>Wrong</button>
          </div>
        </div>

        {/* Right Panel - Suit Selection continued */}
        <div className="suit-panel">
          <button 
            className={`suit-btn ${selectedSuit === 'diamonds' ? 'active' : ''}`}
            onClick={() => setSelectedSuit('diamonds')}
          >
            <span>♦</span> Diamonds
            <small>Intellectual Knowledge</small>
          </button>
          
          <button 
            className={`suit-btn ${selectedSuit === 'clubs' ? 'active' : ''}`}
            onClick={() => setSelectedSuit('clubs')}
          >
            <span>♣</span> Clubs
            <small>Social Knowledge</small>
          </button>
        </div>
      </div>

      {/* Bottom Panel - Reward and Stats */}
      <div className="info-panel">
        {/* Reward Card with Flip Animation */}
        <div 
          className={`info-card ${flippedCards.reward ? 'flipped' : ''}`}
          onClick={() => toggleFlip('reward')}
        >
          <div className="info-card-inner">
            <div className="info-card-front">
              <h3>REWARD</h3>
              <div className="reward-level">
                <span>II</span> 1-30%: Number cards only
              </div>
            </div>
            <div className="info-card-back">
              <ul>
                <li><span>II</span> 30-50%: + Jack cards</li>
                <li><span>A</span> 50-60%: + Queen cards</li>
                <li><span>A</span> 80-100%: + Joker cards</li>
              </ul>
            </div>
          </div>
        </div>

        {/* Stats Card with Flip Animation */}
        <div 
          className={`info-card ${flippedCards.stats ? 'flipped' : ''}`}
          onClick={() => toggleFlip('stats')}
        >
          <div className="info-card-inner">
            <div className="info-card-front">
              <h3>STATS</h3>
              <p>Accuracy: {accuracy}%</p>
              <p>Level: {level}</p>
            </div>
            <div className="info-card-back">
              <p>Unlocked: Number Cards</p>
              <p>Total: 14</p>
              <p>Correct: {correctCount}</p>
              <p>Wrong: {wrongCount}</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default QuizPage;
