import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './NavMenu.css';

function NavMenu() {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [theme, setTheme] = useState('dark');
  const location = useLocation();

  // Apply current theme to body class
  useEffect(() => {
    document.body.className = theme + '-theme';
  }, [theme]);

  // Close sidebar when route changes
  useEffect(() => {
    setIsSidebarOpen(false);
  }, [location.pathname]);

  return (
    <>
      {/* Top Navigation Bar */}
      <header className="topbar">
        <div className="nav-left">
          <button className="hamburger" onClick={() => setIsSidebarOpen(!isSidebarOpen)}>‚ò∞</button>
        </div>
        <div className="nav-right">
          <Link to="/">Flashcards</Link>
          <Link to="/quiz">Quiz</Link>
          <Link to="/create">Create</Link>
          <Link to="/about">About</Link>
          <button className="theme-toggle" onClick={() => setTheme(prev => prev === 'dark' ? 'light' : 'dark')}>
            {theme === 'dark' ? '‚òÄÔ∏è' : 'üåô'}
          </button>
        </div>
      </header>

      {/* Sidebar + Page Shifting */}
      <div className="layout-wrapper">
        {/* Sidebar Slide Panel */}
        <aside className={`sidebar ${isSidebarOpen ? 'visible' : ''}`}>
          <h2>‚ú® Features</h2>
          <ul>
            <li>üÉè Flashcard Flip Animations</li>
            <li>üé® Light / Dark / Mid Theme</li>
            <li>üß† Custom Card Creation</li>
            <li>üîÄ Shuffle & Category Filters</li>
          </ul>
          <button className="sidebar-theme-toggle" onClick={() => setTheme(prev => prev === 'dark' ? 'light' : 'dark')}>
            Toggle Theme
          </button>
        </aside>

        {/* Main Content Area */}
        <main className={`content-area ${isSidebarOpen ? 'shifted' : ''}`}>
          {/* Pages will render below via App.js */}
        </main>
      </div>
    </>
  );
}

export default NavMenu;
