import React, { useState } from 'react';
import decks from '../data/decks';
import Flashcard from '../components/Flashcard';
import './FlashcardPage.css';

function FlashcardPage({ user }) {
  const [selectedSuit, setSelectedSuit] = useState('spades');
  const [cards, setCards] = useState([...decks['spades']]);
  const [current, setCurrent] = useState(0);
  const [showAll, setShowAll] = useState(false);
  const [isShuffled, setIsShuffled] = useState(false);

  const handleSuitChange = (suit) => {
    setSelectedSuit(suit);
    setCards([...decks[suit]]);
    setCurrent(0);
    setShowAll(false);
    setIsShuffled(false);
  };

  const nextCard = () => setCurrent((prev) => (prev + 1) % cards.length);
  const prevCard = () => setCurrent((prev) => (prev - 1 + cards.length) % cards.length);
  
  const shuffleDeck = () => {
    const shuffled = [...cards].sort(() => Math.random() - 0.5);
    setCards(shuffled);
    setCurrent(0);
    setIsShuffled(true);
  };

  const getSuitColor = (suit) => {
    switch(suit) {
      case 'hearts': return '#ff6b6b';
      case 'diamonds': return '#ff9e3f';
      case 'clubs': return '#6bce6b';
      case 'spades': return '#6b8cff';
      default: return '#ffaa33';
    }
  };

  return (
    <div className="flashcard-page-container">
      <header className="flashcard-header">
        <div className="header-content">
          <h1 className="main-title">
            <span className="suit-icon" style={{color: getSuitColor(selectedSuit)}}>
              {selectedSuit === 'hearts' ? 'â™¥' : 
               selectedSuit === 'diamonds' ? 'â™¦' : 
               selectedSuit === 'clubs' ? 'â™£' : 'â™ '}
            </span>
            FlashCard Mastery
          </h1>
          <p className="user-greeting">Welcome back, <span className="username">{user}</span>!</p>
        </div>
      </header>

      <div className="content-wrapper">
        {/* Suit Selection */}
        <div className="suit-selection-container">
          <h2 className="section-title">Choose Your Category</h2>
          <div className="suit-grid">
            {['spades', 'hearts', 'diamonds', 'clubs'].map((suit) => (
              <div 
                key={suit}
                className={`suit-card ${selectedSuit === suit ? 'active' : ''}`}
                onClick={() => handleSuitChange(suit)}
                style={{
                  borderColor: getSuitColor(suit),
                  background: selectedSuit === suit ? 
                    `linear-gradient(135deg, ${getSuitColor(suit)}20, #2a2a2a)` : 
                    '#2a2a2a'
                }}
              >
                <span className="suit-icon-large" style={{color: getSuitColor(suit)}}>
                  {suit === 'hearts' ? 'â™¥' : 
                   suit === 'diamonds' ? 'â™¦' : 
                   suit === 'clubs' ? 'â™£' : 'â™ '}
                </span>
                <h3 className="suit-title">
                  {suit.charAt(0).toUpperCase() + suit.slice(1)}
                </h3>
                <p className="suit-description">
                  {suit === 'spades' && 'Physical & Action-Oriented Knowledge'}
                  {suit === 'hearts' && 'Emotional & Psychological Knowledge'}
                  {suit === 'diamonds' && 'Intellectual & Strategic Knowledge'}
                  {suit === 'clubs' && 'Balanced & Social Knowledge'}
                </p>
              </div>
            ))}
          </div>
        </div>

        {/* Deck Controls */}
        <div className="deck-controls">
          <button 
            onClick={shuffleDeck}
            className={`control-btn ${isShuffled ? 'active' : ''}`}
          >
            <span className="btn-icon">ðŸ”€</span> Shuffle Deck
          </button>
          <button 
            onClick={() => setShowAll(prev => !prev)}
            className={`control-btn ${showAll ? 'active' : ''}`}
          >
            <span className="btn-icon">{showAll ? 'ðŸ“‡' : 'ðŸ“š'}</span>
            {showAll ? 'Show Single Card' : 'Show All Cards'}
          </button>
        </div>

        {/* Flashcard Display */}
        <div className="flashcard-display-area">
          {!showAll ? (
            <>
              <div className="flashcard-container">
                <Flashcard
                  front={cards[current].front}
                  back={cards[current].back}
                  suit={selectedSuit}
                  rank={cards[current].rank}
                />
              </div>
              <div className="navigation-controls">
                <button onClick={prevCard} className="nav-btn prev-btn">
                  â—€ Previous
                </button>
                <div className="card-counter">
                  Card <span>{current + 1}</span> of <span>{cards.length}</span>
                </div>
                <button onClick={nextCard} className="nav-btn next-btn">
                  Next â–¶
                </button>
              </div>
            </>
          ) : (
            <div className="card-gallery">
              {cards.map((card, index) => (
                <div key={index} className="gallery-card-wrapper">
                  <Flashcard
                    front={card.front}
                    back={card.back}
                    suit={selectedSuit}
                    rank={card.rank}
                  />
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default FlashcardPage;
