import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './NavMenu.css';

function NavMenu() {
  const [isOpen, setIsOpen] = useState(false);
  const [theme, setTheme] = useState('dark');
  const location = useLocation();

  useEffect(() => {
    document.body.className = theme + '-theme';
  }, [theme]);

  useEffect(() => {
    setIsOpen(false); // close panel when navigating
  }, [location.pathname]);

  return (
    <>
      <header className="navbar">
        <div className="hamburger" onClick={() => setIsOpen(!isOpen)}>‚ò∞</div>
        <div className="nav-right">
          <Link to="/">Flashcards</Link>
          <Link to="/quiz">Quiz</Link>
          <Link to="/create">Create</Link>
          <Link to="/about">About</Link>
        </div>
      </header>

      <div className={`sidebar-layout ${isOpen ? 'open' : ''}`}>
        <aside className="sidebar">
          <h2>‚ú® Features</h2>
          <ul>
            <li>üÉè Card flipping</li>
            <li>üåò Light/Mid/Dark themes</li>
            <li>üîÄ Shuffle by suit</li>
            <li>üß† Create your own cards</li>
          </ul>
          <button onClick={() => setTheme(t => t === 'dark' ? 'light' : 'dark')}>
            {theme === 'dark' ? '‚òÄÔ∏è Light Mode' : 'üåô Dark Mode'}
          </button>
        </aside>

        <main className="content">
          {/* This is where all your Routes render */}
        </main>
      </div>
    </>
  );
}

export default NavMenu;
