import React, { useState } from 'react';
import decks from '../data/decks';
import Flashcard from '../components/Flashcard';
import './FlashcardPage.css';

function FlashcardPage({ user }) {
  const [selectedSuit, setSelectedSuit] = useState('spades');
  const [cards, setCards] = useState([...decks['spades']]);
  const [current, setCurrent] = useState(0);
  const [showAll, setShowAll] = useState(false);

  const suitColors = {
    spades: '#4e79a7',
    hearts: '#e15759',
    diamonds: '#f28e2b',
    clubs: '#59a14f',
    all: '#79706e'
  };

  const handleSuitChange = (suit) => {
    setSelectedSuit(suit);
    setCards([...decks[suit]]);
    setCurrent(0);
    setShowAll(false);
  };

  const nextCard = () => setCurrent((prev) => (prev + 1) % cards.length);
  const prevCard = () => setCurrent((prev) => (prev - 1 + cards.length) % cards.length);
  
  const shuffleDeck = () => {
    const shuffled = [...cards].sort(() => Math.random() - 0.5);
    setCards(shuffled);
    setCurrent(0);
  };

  const addAllCards = () => {
    const allCards = [
      ...decks['spades'],
      ...decks['hearts'],
      ...decks['diamonds'],
      ...decks['clubs']
    ];
    setCards(allCards);
    setCurrent(0);
    setSelectedSuit('all');
  };

  return (
    <div className="flashcard-app" style={{ backgroundColor: '#f8f9fa' }}>
      <header className="app-header">
        <h1 className="app-title">
          <span style={{ color: suitColors[selectedSuit] }}>Flash</span>Quiz Master
        </h1>
        <p className="welcome-message">Welcome, <span className="username">{user}</span>!</p>
      </header>

      <div className="app-container">
        {/* Left Column - Spades & Hearts */}
        <div className="column left-column">
          <div 
            className={`suit-card ${selectedSuit === 'spades' ? 'active' : ''}`}
            onClick={() => handleSuitChange('spades')}
            style={{ 
              backgroundColor: selectedSuit === 'spades' ? '#e8f0f9' : 'white',
              borderColor: suitColors.spades
            }}
          >
            <span className="suit-icon" style={{ color: suitColors.spades }}>♠</span>
            <h3 style={{ color: suitColors.spades }}>Spades</h3>
            <p>Physical Knowledge</p>
          </div>
          
          <div 
            className={`suit-card ${selectedSuit === 'hearts' ? 'active' : ''}`}
            onClick={() => handleSuitChange('hearts')}
            style={{ 
              backgroundColor: selectedSuit === 'hearts' ? '#fbecec' : 'white',
              borderColor: suitColors.hearts
            }}
          >
            <span className="suit-icon" style={{ color: suitColors.hearts }}>♥</span>
            <h3 style={{ color: suitColors.hearts }}>Hearts</h3>
            <p>Emotional Knowledge</p>
          </div>
        </div>

        {/* Middle Column - Flashcard & Controls */}
        <div className="column middle-column">
          <div className="card-controls">
            <button 
              onClick={shuffleDeck}
              style={{ backgroundColor: suitColors[selectedSuit] }}
            >
              <span className="control-icon">🔀</span> Shuffle Deck
            </button>
            <button 
              onClick={() => setShowAll(!showAll)}
              style={{ backgroundColor: suitColors[selectedSuit] }}
            >
              <span className="control-icon">{showAll ? '📇' : '📚'}</span>
              {showAll ? 'Show Single' : 'Show All'}
            </button>
            <button 
              onClick={addAllCards}
              style={{ backgroundColor: suitColors.all }}
            >
              <span className="control-icon">🃏</span> All 52 Cards
            </button>
          </div>

          <div className="flashcard-container">
            {!showAll ? (
              <div className="single-card-view">
                <Flashcard
                  front={cards[current].front}
                  back={cards[current].back}
                  suit={selectedSuit}
                  rank={cards[current].rank}
                  color={suitColors[selectedSuit]}
                />
                <div className="card-navigation">
                  <button 
                    onClick={prevCard}
                    style={{ backgroundColor: suitColors[selectedSuit] }}
                  >
                    ← Previous
                  </button>
                  <span className="counter">
                    Card <strong>{current + 1}</strong> of <strong>{cards.length}</strong>
                  </span>
                  <button 
                    onClick={nextCard}
                    style={{ backgroundColor: suitColors[selectedSuit] }}
                  >
                    Next →
                  </button>
                </div>
              </div>
            ) : (
              <div className="all-cards-view">
                {cards.map((card, index) => (
                  <div key={index} className="small-flashcard-wrapper">
                    <Flashcard
                      front={card.front}
                      back={card.back}
                      suit={card.suit || selectedSuit}
                      rank={card.rank}
                      small
                      color={suitColors[card.suit || selectedSuit]}
                    />
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Right Column - Diamonds & Clubs */}
        <div className="column right-column">
          <div 
            className={`suit-card ${selectedSuit === 'diamonds' ? 'active' : ''}`}
            onClick={() => handleSuitChange('diamonds')}
            style={{ 
              backgroundColor: selectedSuit === 'diamonds' ? '#fdf2e6' : 'white',
              borderColor: suitColors.diamonds
            }}
          >
            <span className="suit-icon" style={{ color: suitColors.diamonds }}>♦</span>
            <h3 style={{ color: suitColors.diamonds }}>Diamonds</h3>
            <p>Intellectual Knowledge</p>
          </div>
          
          <div 
            className={`suit-card ${selectedSuit === 'clubs' ? 'active' : ''}`}
            onClick={() => handleSuitChange('clubs')}
            style={{ 
              backgroundColor: selectedSuit === 'clubs' ? '#eef7eb' : 'white',
              borderColor: suitColors.clubs
            }}
          >
            <span className="suit-icon" style={{ color: suitColors.clubs }}>♣</span>
            <h3 style={{ color: suitColors.clubs }}>Clubs</h3>
            <p>Social Knowledge</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default FlashcardPage;
