import React, { useState } from 'react';
import decks from '../data/decks';
import Flashcard from '../components/Flashcard';
import './FlashcardPage.css';

function QuizPage({ user }) {
  const [selectedSuit, setSelectedSuit] = useState('spades');
  const [cards, setCards] = useState([...decks['spades']]);
  const [current, setCurrent] = useState(0);
  const [correctCount, setCorrectCount] = useState(0);
  const [wrongCount, setWrongCount] = useState(0);
  const [showAll, setShowAll] = useState(false);

  const accuracy = ((correctCount / (correctCount + wrongCount)) * 100 || 0).toFixed(0);
  const level =
    accuracy >= 100
      ? '♠ Ace'
      : accuracy >= 75
      ? '♦ Prodigy'
      : accuracy >= 50
      ? '♥ Rookie'
      : '♣ Novice';

      const unlockedRanks = [];

if (accuracy >= 1) unlockedRanks.push('🃏 Number Cards');
if (accuracy >= 30) unlockedRanks.push('🃏 + Jack');
if (accuracy >= 50) unlockedRanks.push('🃏 + Queen');
if (accuracy >= 60) unlockedRanks.push('🃏 + King');
if (accuracy >= 80) unlockedRanks.push('🃏 + Joker');


  const handleSuitChange = (suit) => {
    setSelectedSuit(suit);
    const shuffled = [...decks[suit]];
    setCards(shuffled);
    setCurrent(0);
    setCorrectCount(0);
    setWrongCount(0);
  };

  const nextCard = () => setCurrent((prev) => (prev + 1) % cards.length);
  const prevCard = () => setCurrent((prev) => (prev - 1 + cards.length) % cards.length);
  const shuffleDeck = () => {
    const shuffled = [...cards].sort(() => Math.random() - 0.5);
    setCards(shuffled);
    setCurrent(0);
  };

  return (
    <div className="page-wrapper">
      <header className="page-header">
        <h1 className="arcade-title">♠️ FlashQuizApp ♥️</h1>
        <p className="tagline">Welcome, <strong>{user}</strong> 👋</p>
      </header>

      <div className="deck-toolbar">
        <button onClick={shuffleDeck}>🔀 Shuffle</button>
        <button onClick={() => setShowAll((prev) => !prev)}>
          {showAll ? '📇 Hide All Cards' : '📇 Show All Cards'}
        </button>
      </div>

      <div className="quiz-grid">
        {/* 1️⃣ Reward Box */}
       <div className="reward-card">
  <h3>🎁 REWARD</h3>
  <ul>
    <li>🂠 1–30% → Number cards only</li>
    <li>🂫 30–50% → + Jack cards</li>
    <li>👸 50–60% → + Queen cards</li>
    <li>🤴 60–80% → + King cards</li>
    <li>🃏 80–100% → + Joker unlocked!</li>
  </ul>
</div>


        {/* 2️⃣ Left Suit Selector */}
        <div className="quiz-column suit-side left">
          <div className="suit-card" onClick={() => handleSuitChange('spades')}>
            <span className="suit-icon">♠</span>
            <h3>Spades</h3>
            <p>Physical Knowledge</p>
          </div>
          <div className="suit-card" onClick={() => handleSuitChange('hearts')}>
            <span className="suit-icon">♥</span>
            <h3>Hearts</h3>
            <p>Emotional Knowledge</p>
          </div>
        </div>

        {/* 3️⃣ Flashcard Game Area */}
        <div className="quiz-column quiz-center">
          <div className="answer-buttons">
            <button className="answer correct" onClick={() => { setCorrectCount(c => c + 1); nextCard(); }}>
              ✅ Correct
            </button>
            <button className="answer wrong" onClick={() => { setWrongCount(w => w + 1); nextCard(); }}>
              ❌ Wrong
            </button>
          </div>

          

          <div className="flashcard-wrapper">
            <Flashcard
              front={cards[current].front}
              back={cards[current].back}
              suit={selectedSuit}
              rank={cards[current].rank}
            />
          </div>

          <div className="nav-buttons">
            <button onClick={prevCard}>⬅️ Prev</button>

             <span className="card-counter">
    Card {current + 1} / {cards.length}
  </span>

            <button onClick={nextCard}>Next ➡️</button>
          </div>
        </div>

        {/* 4️⃣ Right Suit Selector */}
        <div className="quiz-column suit-side right">
          <div className="suit-card" onClick={() => handleSuitChange('diamonds')}>
            <span className="suit-icon">♦</span>
            <h3>Diamonds</h3>
            <p>Intellectual Knowledge</p>
          </div>
          <div className="suit-card" onClick={() => handleSuitChange('clubs')}>
            <span className="suit-icon">♣</span>
            <h3>Clubs</h3>
            <p>Social Knowledge</p>
          </div>
        </div>

        {/* 5️⃣ Stats Box */}
        <div className="quiz-column stats-card">
          <h3>📊 STATS</h3>
          <p>🎯 Accuracy: {accuracy}%</p>
<p>🚀 Level: {level}</p>
<p>📑 Unlocked: <br />{unlockedRanks.join(', ')}</p>
          <p>Total: {cards.length}</p>
          <p>✅ Correct: {correctCount}</p>
          <p>❌ Wrong: {wrongCount}</p>
          
          <p>🚀 Level: {level}</p>
        </div>
      </div>
    </div>
  );
}

export default QuizPage;
