import React, { useState } from 'react';
import Flashcard from '../components/Flashcard';
import decks from '../data/decks';
import './FlashcardPage.css'; // uses your styling

function QuizModePage({ user }) {
  const [selectedSuit, setSelectedSuit] = useState('spades');
  const [cards, setCards] = useState([...decks[selectedSuit]]);
  const [current, setCurrent] = useState(0);
  const [correctCount, setCorrectCount] = useState(1);
  const [wrongCount, setWrongCount] = useState(99);
  const [showAll, setShowAll] = useState(false);
  const [statsFlipped, setStatsFlipped] = useState(false);

  const accuracy = ((correctCount / (correctCount + wrongCount)) * 100 || 0).toFixed(0);
  const acc = parseFloat(accuracy);

  const unlockedTypes = [];
  if (acc > 0 && acc <= 30) unlockedTypes.push('number');
  if (acc > 30 && acc <= 50) unlockedTypes.push('jack');
  if (acc > 50 && acc <= 60) unlockedTypes.push('queen');
  if (acc > 60 && acc <= 80) unlockedTypes.push('king');
  if (acc > 80 && acc <= 100) unlockedTypes.push('joker');

  const filtered = cards.map(c => {
    const r = c.rank?.toLowerCase();
    return { ...c, type: isNaN(r) ? r : 'number' };
  });
  const visible = filtered.filter(c => unlockedTypes.includes(c.type));
  const fallback = {
    front: 'üîí No cards unlocked yet',
    back: 'Answer correctly to unlock!',
    suit: selectedSuit,
    rank: 'locked',
  };

  const active = visible.length > 0 ? visible[current % visible.length] : fallback;

  const handleCorrect = () => {
    setCorrectCount(c => c + 1);
    setCurrent(c => (c + 1) % Math.max(visible.length, 1));
  };
  const handleWrong = () => {
    setWrongCount(c => c + 1);
    setCurrent(c => (c + 1) % Math.max(visible.length, 1));
  };
  const shuffleDeck = () => {
    const shuffled = [...cards].sort(() => Math.random() - 0.5);
    setCards(shuffled);
    setCurrent(0);
  };

  return (
    <div className="page-wrapper">
      <header className="page-header"><h1>Quiz Mode üÉè</h1></header>

      <div className="quiz-grid">
        {/* LEFT */}
        <div className="quiz-column suit-side left">
          <div className="suit-card" onClick={() => setSelectedSuit('spades')}>
            <span className="suit-icon">‚ô†</span>
            <h3>Spades</h3>
          </div>
          <div className="suit-card" onClick={() => setSelectedSuit('hearts')}>
            <span className="suit-icon">‚ô•</span>
            <h3>Hearts</h3>
          </div>
        </div>

        {/* CENTER */}
        <div className="quiz-column quiz-center">
          <div className="deck-toolbar">
            <button onClick={shuffleDeck}>üîÄ Shuffle</button>
            <button onClick={() => setShowAll(p => !p)}>
              {showAll ? 'üìá Hide All' : 'üìá Show All'}
            </button>
          </div>

          <div className="flashcard-wrapper">
            <Flashcard
              front={active.front}
              back={active.back}
              suit={active.suit}
              rank={active.rank}
            />
          </div>

          <div className="nav-buttons">
            <button onClick={() => setCurrent((c) => (c - 1 + visible.length) % visible.length)}>‚¨ÖÔ∏è Prev</button>
            <button onClick={handleCorrect}>‚úÖ Correct</button>
            <span className="card-counter">
              {visible.length > 0 ? `Card ${current + 1} / ${visible.length}` : 'No cards'}
            </span>
            <button onClick={handleWrong}>‚ùå Wrong</button>
            <button onClick={() => setCurrent((c) => (c + 1) % visible.length)}>Next ‚û°Ô∏è</button>
          </div>
        </div>

        {/* RIGHT */}
        <div className="quiz-column suit-side right">
          <div className="suit-card" onClick={() => setSelectedSuit('diamonds')}>
            <span className="suit-icon">‚ô¶</span>
            <h3>Diamonds</h3>
          </div>
          <div className="suit-card" onClick={() => setSelectedSuit('clubs')}>
            <span className="suit-icon">‚ô£</span>
            <h3>Clubs</h3>
          </div>
        </div>

        {/* STATS CARD */}
        <div className="quiz-column stats-card" onClick={() => setStatsFlipped(f => !f)}>
          <div className={`flip-card ${statsFlipped ? 'flipped' : ''}`}>
            <div className="flip-card-inner">
              <div className="flip-card-front">
                <h3>üìä STATS</h3>
                <p>üéØ Accuracy: {accuracy}%</p>
                <p>‚úÖ Correct: {correctCount}</p>
                <p>‚ùå Wrong: {wrongCount}</p>
              </div>
              <div className="flip-card-back">
                <h3>üéÅ REWARD SYSTEM</h3>
                <ul>
                  <li>üÇ† 1‚Äì30% ‚Üí Number cards</li>
                  <li>üÇ´ 30‚Äì50% ‚Üí + Jack</li>
                  <li>üë∏ 50‚Äì60% ‚Üí + Queen</li>
                  <li>ü§¥ 60‚Äì80% ‚Üí + King</li>
                  <li>üÉè 80‚Äì100% ‚Üí + Joker</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default QuizModePage;
