import React, { useState } from 'react';
import decks from '../data/decks';
import Flashcard from '../components/Flashcard';
import './FlashcardPage.css';

function FlashcardPage({ user }) {
  const [selectedSuit, setSelectedSuit] = useState('spades');
  const [cards, setCards] = useState([...decks['spades']]);
  const [current, setCurrent] = useState(0);
  const [showAll, setShowAll] = useState(false);

  const suitColors = {
    spades: '#4e79a7',
    hearts: '#e15759',
    diamonds: '#f28e2b',
    clubs: '#59a14f',
    all: '#79706e'
  };

  const handleSuitChange = (suit) => {
    setSelectedSuit(suit);
    setCards([...decks[suit]]);
    setCurrent(0);
    setShowAll(false);
  };

  const nextCard = () => setCurrent((prev) => (prev + 1) % cards.length);
  const prevCard = () => setCurrent((prev) => (prev - 1 + cards.length) % cards.length);
  
  const shuffleDeck = () => {
    const shuffled = [...cards].sort(() => Math.random() - 0.5);
    setCards(shuffled);
    setCurrent(0);
  };

  const addAllCards = () => {
    const allCards = [
      ...decks['spades'],
      ...decks['hearts'],
      ...decks['diamonds'],
      ...decks['clubs']
    ];
    setCards(allCards);
    setCurrent(0);
    setSelectedSuit('all');
  };

  return (
    <div className="flashcard-app">
      <div className="app-header">
        <h1>FlashQuiz Master</h1>
        <p>Welcome, <strong>{user}</strong></p>
      </div>

      <div className="main-layout">
        {/* Left Column - Compact Suit Selectors */}
        <div className="suit-column left-suits">
          <div 
            className={`suit-selector ${selectedSuit === 'spades' ? 'active' : ''}`}
            onClick={() => handleSuitChange('spades')}
            style={{ borderColor: suitColors.spades }}
          >
            <h3 style={{ color: suitColors.spades }}>Spades</h3>
            <p>Physical Knowledge</p>
          </div>
          
          <div 
            className={`suit-selector ${selectedSuit === 'hearts' ? 'active' : ''}`}
            onClick={() => handleSuitChange('hearts')}
            style={{ borderColor: suitColors.hearts }}
          >
            <h3 style={{ color: suitColors.hearts }}>Hearts</h3>
            <p>Emotional Knowledge</p>
          </div>
        </div>

        {/* Main Content Area */}
        <div className="main-content">
          <div className="card-controls">
            <button 
              onClick={shuffleDeck}
              style={{ backgroundColor: suitColors[selectedSuit] }}
            >
              Shuffle Deck
            </button>
            <button 
              onClick={() => setShowAll(!showAll)}
              style={{ backgroundColor: suitColors[selectedSuit] }}
            >
              {showAll ? 'Show Single' : 'Show All'}
            </button>
            <button 
              onClick={addAllCards}
              style={{ backgroundColor: suitColors.all }}
            >
              All 52 Cards
            </button>
          </div>

          <div className="flashcard-display">
            {!showAll ? (
              <>
                <Flashcard
                  front={cards[current].front}
                  back={cards[current].back}
                  suit={selectedSuit}
                  rank={cards[current].rank}
                  color={suitColors[selectedSuit]}
                />
                <div className="card-navigation">
                  <button onClick={prevCard}>Previous</button>
                  <span>Card {current + 1} of {cards.length}</span>
                  <button onClick={nextCard}>Next</button>
                </div>
              </>
            ) : (
              <div className="all-cards-view">
                {cards.map((card, index) => (
                  <Flashcard
                    key={index}
                    front={card.front}
                    back={card.back}
                    suit={card.suit || selectedSuit}
                    rank={card.rank}
                    small
                    color={suitColors[card.suit || selectedSuit]}
                  />
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Right Column - Compact Suit Selectors */}
        <div className="suit-column right-suits">
          <div 
            className={`suit-selector ${selectedSuit === 'diamonds' ? 'active' : ''}`}
            onClick={() => handleSuitChange('diamonds')}
            style={{ borderColor: suitColors.diamonds }}
          >
            <h3 style={{ color: suitColors.diamonds }}>Diamonds</h3>
            <p>Intellectual Knowledge</p>
          </div>
          
          <div 
            className={`suit-selector ${selectedSuit === 'clubs' ? 'active' : ''}`}
            onClick={() => handleSuitChange('clubs')}
            style={{ borderColor: suitColors.clubs }}
          >
            <h3 style={{ color: suitColors.clubs }}>Clubs</h3>
            <p>Social Knowledge</p>
          </div>
        </div>
      </div>

      <div className="app-footer">
        <span>27</span>
        <span>ENG</span>
        <span>US</span>
        <span>11:44 PM</span>
        <span>17/2025</span>
      </div>
    </div>
  );
}

export default FlashcardPage;
