import React, { useState } from 'react';
import decks from '../data/decks';
import Flashcard from '../components/Flashcard';
import './FlashcardPage.css';

function FlashcardPage({ user }) {
  const [selectedSuit, setSelectedSuit] = useState('spades');
  const [cards, setCards] = useState([...decks['spades']]);
  const [current, setCurrent] = useState(0);
  const [showAll, setShowAll] = useState(false);

  const handleSuitChange = (suit) => {
    setSelectedSuit(suit);
    setCards([...decks[suit]]);
    setCurrent(0);
    setShowAll(false);
  };

  const nextCard = () => setCurrent((prev) => (prev + 1) % cards.length);
  const prevCard = () => setCurrent((prev) => (prev - 1 + cards.length) % cards.length);
  
  const shuffleDeck = () => {
    const shuffled = [...cards].sort(() => Math.random() - 0.5);
    setCards(shuffled);
    setCurrent(0);
  };

  const addAllCards = () => {
    const allCards = [
      ...decks['spades'],
      ...decks['hearts'],
      ...decks['diamonds'],
      ...decks['clubs']
    ];
    setCards(allCards);
    setCurrent(0);
    setSelectedSuit('all');
  };

  return (
    <div className="flashcard-app">
      <h1 className="app-title">FlashQuiz App</h1>
      
      <div className="app-container">
        {/* Left Column - Spades & Hearts */}
        <div className="column left-column">
          <div 
            className={`suit-card ${selectedSuit === 'spades' ? 'active' : ''}`}
            onClick={() => handleSuitChange('spades')}
          >
            <span className="suit-icon">♠</span>
            <h3>Spades</h3>
            <p>Physical Knowledge</p>
          </div>
          
          <div 
            className={`suit-card ${selectedSuit === 'hearts' ? 'active' : ''}`}
            onClick={() => handleSuitChange('hearts')}
          >
            <span className="suit-icon">♥</span>
            <h3>Hearts</h3>
            <p>Emotional Knowledge</p>
          </div>
        </div>

        {/* Middle Column - Flashcard & Controls */}
        <div className="column middle-column">
          <div className="card-controls">
            <button onClick={shuffleDeck}>Shuffle Deck</button>
            <button onClick={() => setShowAll(!showAll)}>
              {showAll ? 'Show Single' : 'Show All'}
            </button>
            <button onClick={addAllCards}>All 52 Cards</button>
          </div>

          <div className="flashcard-container">
            {!showAll ? (
              <div className="single-card-view">
                <Flashcard
                  front={cards[current].front}
                  back={cards[current].back}
                  suit={selectedSuit}
                  rank={cards[current].rank}
                />
                <div className="card-navigation">
                  <button onClick={prevCard}>← Previous</button>
                  <span>Card {current + 1} of {cards.length}</span>
                  <button onClick={nextCard}>Next →</button>
                </div>
              </div>
            ) : (
              <div className="all-cards-view">
                {cards.map((card, index) => (
                  <div key={index} className="small-flashcard-wrapper">
                    <Flashcard
                      front={card.front}
                      back={card.back}
                      suit={card.suit || selectedSuit}
                      rank={card.rank}
                      small
                    />
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* Right Column - Diamonds & Clubs */}
        <div className="column right-column">
          <div 
            className={`suit-card ${selectedSuit === 'diamonds' ? 'active' : ''}`}
            onClick={() => handleSuitChange('diamonds')}
          >
            <span className="suit-icon">♦</span>
            <h3>Diamonds</h3>
            <p>Intellectual Knowledge</p>
          </div>
          
          <div 
            className={`suit-card ${selectedSuit === 'clubs' ? 'active' : ''}`}
            onClick={() => handleSuitChange('clubs')}
          >
            <span className="suit-icon">♣</span>
            <h3>Clubs</h3>
            <p>Social Knowledge</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default FlashcardPage;
