import React, { useState } from 'react';
import decks from '../data/decks';
import Flashcard from '../components/Flashcard';
import './FlashcardPage.css';

function FlashcardPage({ user }) {
  const [suit, setSuit] = useState(null);
  const [cards, setCards] = useState([]);
  const [current, setCurrent] = useState(0);

  const chooseDeck = (chosenSuit) => {
    const selected = [...decks[chosenSuit]];
    setCards(shuffle(selected));
    setSuit(chosenSuit);
    setCurrent(0);
  };

  const shuffle = (arr) => arr.sort(() => Math.random() - 0.5);

  const nextCard = () => setCurrent((prev) => (prev + 1) % cards.length);

  return (
    <div className="page-wrapper">
      <header className="page-header">
        <h1 className="arcade-title">‚ô†Ô∏è FlashQuizApp ‚ô•Ô∏è</h1>
        <p className="tagline">Welcome, <strong>{user}</strong> üëã</p>
      </header>

      {!suit ? (
        <div className="deck-selector">
          <h3>Choose Your Deck</h3>
          <button onClick={() => chooseDeck('spades')}>‚ô†Ô∏è Spades</button>
          <button onClick={() => chooseDeck('hearts')}>‚ô•Ô∏è Hearts</button>
          <button onClick={() => chooseDeck('diamonds')}>‚ô¶Ô∏è Diamonds</button>
          <button onClick={() => chooseDeck('clubs')}>‚ô£Ô∏è Clubs</button>
        </div>
      ) : (
        <div className="flashcard-zone">
          <Flashcard
            front={cards[current].front}
            back={cards[current].back}
            suit={suit}
            rank={cards[current].rank}
          />
          <button onClick={nextCard}>Next Card üîÅ</button>
        </div>
      )}
    </div>
  );
}

export default FlashcardPage;
