import React, { useState } from 'react';
import decks from '../data/decks';
import Flashcard from '../components/Flashcard';
import './FlashcardPage.css';

function FlashcardPage({ user }) {
  const [selectedSuit, setSelectedSuit] = useState('spades');
  const [cards, setCards] = useState([...decks['spades']]);
  const [current, setCurrent] = useState(0);
  const [showAll, setShowAll] = useState(false);

  const handleSuitChange = (suit) => {
    setSelectedSuit(suit);
    const shuffled = [...decks[suit]];
    setCards(shuffled);
    setCurrent(0);
    setShowAll(false);
  };

  const nextCard = () => setCurrent((prev) => (prev + 1) % cards.length);
  const prevCard = () => setCurrent((prev) => (prev - 1 + cards.length) % cards.length);
  const shuffleDeck = () => {
    const shuffled = [...cards].sort(() => Math.random() - 0.5);
    setCards(shuffled);
    setCurrent(0);
  };

  return (
    <div className="page-wrapper">
      <header className="page-header">
        <h1 className="arcade-title">‚ô†Ô∏è FlashQuizApp ‚ô•Ô∏è</h1>
        <p className="tagline">Welcome, <strong>{user}</strong> üëã</p>
      </header>

      {/* üîÅ Deck Controls */}
      <div className="deck-toolbar">
        {['spades', 'hearts', 'diamonds', 'clubs'].map((suit) => (
          <button
            key={suit}
            onClick={() => handleSuitChange(suit)}
            className={selectedSuit === suit ? 'active' : ''}
          >
            {suit === 'spades' && '‚ô† Spades'}
            {suit === 'hearts' && '‚ô• Hearts'}
            {suit === 'diamonds' && '‚ô¶ Diamonds'}
            {suit === 'clubs' && '‚ô£ Clubs'}
          </button>
        ))}
        <button onClick={shuffleDeck}>üîÄ Shuffle</button>
        <button onClick={() => setShowAll((prev) => !prev)}>
          {showAll ? 'üìá Hide All Cards' : 'üìá Show All Cards'}
        </button>
      </div>

      {/* üÉè Flashcard Display */}
      {!showAll ? (
        <div className="flashcard-zone">
          <Flashcard
            front={cards[current].front}
            back={cards[current].back}
            suit={selectedSuit}
            rank={cards[current].rank}
          />
          <div className="nav-buttons">
            <button onClick={prevCard}>‚¨ÖÔ∏è Prev</button>
            <span className="card-counter">
              Card {current + 1} / {cards.length}
            </span>
            <button onClick={nextCard}>Next ‚û°Ô∏è</button>
          </div>
        </div>
      ) : (
        <div className="card-gallery">
          {cards.map((card, index) => (
            <Flashcard
              key={index}
              front={card.front}
              back={card.back}
              suit={selectedSuit}
              rank={card.rank}
            />
          ))}
        </div>
      )}
    </div>
  );
}

export default FlashcardPage;
